import "axios";

// Args
exports.required = ['message', 'experienceId']
//exports.optional = ['']

// Docs
/**
 * ðŸ“¨ Send a message to live servers
 * @param {string} message
 * @param {number} experienceId
**/

export function sendMessage (message, experienceId) {
    axios.post(`https://apis.roblox.com/messaging-service/v1/universes/${experienceId}/topics/roranking`, {
        message
    }, {
        headers: {
            "x-api-key": Tools.config.Roblox.APIKey,
            "Content-Type": "application/json"
        }
    })
    .then(function (response) {
        //console.log(response.data);
        return response.data;
    })
    .catch(function (error) {
        console.log(error);
        return error ;
    });
};